import os
import subprocess

SEARCH = b"system:"
TERM_LINE = "Name"


def copynet(netint):
    li=[]
    for i in range(len(netint)):
        if SEARCH in netint[i]:
            break
    for i in range(i+2,20):
        li.append(netint[i].decode('UTF-8'))
    return(li)

def get_item1(i):
    st=li[i]
    a=st.split(":")
    ans=a[0].strip()
    return (ans)


def get_item2(i):
    st=li[i]
    a=st.split(":")
    ans=a[-1]
    if i==3 or i==6:
        ans=a[1]+":"+a[2]+":"+a[3]+":"+a[4]+":"+a[5]+":"+a[6]
    return (ans.strip())

COMMAND = "netsh wlan show interface"
netint = subprocess.check_output(COMMAND).split(b"\n")
li=copynet(netint)


